

QUnit.test( "test API - hideByIds", function( assert ) {
    var helper = new FilterTestHelper();

    var filter = new CLOUD.FilterManager();
    filter.clear();

    var hiddenIds = [3, 5];
    filter.hideByIds(hiddenIds);

    var expectVisibleIds = [0, 1, 2, 4, 6, 7, 8, 9];
    //filterTestCheckResult(assert, helper.getObjects(), filter._isHiddenFileId.bind(filter), expectHiddenIds, "visible file id");
    filterTestCheckResult(assert, helper.getObjects(), filter._isVisible.bind(filter), expectVisibleIds, "hide by ids");
});